"use strict";var user=document.querySelector(".user"),pass=document.querySelector(".pass"),btn=document.querySelector(".btn");btn.onclick=function(){if(!user.value||!pass.value)return alert("账号或密码不能为空！"),!1;$.ajax({url:"../data/data.php",type:"get",data:{type:"login",user:user.value,pass:pass.value},dataType:"json",success:function(e){alert(e.msg),3===e.err&&(location.href='./index.html?name="123"')},error:function(){return alert("提交失败"),!1}})};